import { createWebHistory, createRouter } from "vue-router";

// layouts
import Admin1 from "@/layouts/Admin_v.vue";
import Auth from "@/layouts/Auth.vue";

// views for Admin layout
import Dashboard1 from "@/views/admin_/Dashboard.vue";
import Settings from "@/views/admin_/Settings.vue";
import Tables from "@/views/admin_/Tables.vue";
import Maps from "@/views/admin_/Maps.vue";

// views for Auth layout
import Login from "@/views/auth/Login.vue";
import Register from "@/views/auth/Register.vue";

// views without layouts
import Landing from "@/views/Landing.vue";
import Profile from "@/views/Profile.vue";
import Home from "@/views/Index.vue";
import indexTemp from "@/views/Index_temp.vue";

//coba
import Admin from "@/layouts/Admin.vue";
import Dashboard from "@/views/admin/Dashboard.vue";
import Client from "@/views/admin/ManageClient.vue";
import Owner from "@/views/admin/ManageOwner.vue";
import Place from "@/views/admin/ManagePlace.vue";
import Address from "@/views/admin/ManageAddress.vue";
import Facility from "@/views/admin/ManageFacility.vue";
import Order from "@/views/admin/ManageOrder.vue";

// routes

const router = createRouter({
    history: createWebHistory(),
    routes: [{
        path: "/admin1",
        redirect: "/admin1/dashboard",
        component: Admin1,
        children: [{
                path: "/admin1/dashboard",
                component: Dashboard1,
            },
            {
                path: "/admin1/settings",
                component: Settings,
            },
            {
                path: "/admin1/tables",
                component: Tables,
            },
            {
                path: "/admin1/maps",
                component: Maps,
            },
        ],
    }, {
        path: "/admin",
        redirect: "/admin/dashboard",
        component: Admin,
        children: [{
                path: "/admin/dashboard",
                component: Dashboard,
            }, {
                path: "/admin/client",
                component: Client,
            },
            {
                path: "/admin/owner",
                component: Owner,
            },
            {
                path: "/admin/place",
                component: Place,
            },
            {
                path: "/admin/address",
                component: Address,
            },
            {
                path: "/admin/facility",
                component: Facility,
            },
            {
                path: "/admin/Order",
                component: Order,
            },
        ],
        meta: {
            requiresAuth: true,
            roles: "Admin",
        }
    }, {
        path: "/auth",
        redirect: "/auth/login",
        component: Auth,
        children: [{
                path: "/auth/login",
                component: Login,
            },
            {
                path: "/auth/register",
                component: Register,
            },
        ],
        meta: {
            guest: true,
        }
    }, {
        path: "/landing",
        component: Landing,
        meta: {
            requiresAuth: true,
        }
    }, {
        path: "/temp",
        component: indexTemp,
    }, {
        path: "/profile",
        component: Profile,
        meta: {
            requiresAuth: true,
        }
    }, {
        path: "/",
        component: Home,
    }]
});

// import { useSwal } from "@/plugins/useSwal.js";

router.beforeEach((to, from, next) => {
    let role = localStorage.getItem('role'); //<--Get role from API results
    let jwt = localStorage.getItem('jwt');

    if (to.meta.requiresAuth) {
        if (!role || !jwt) {
            router.push({ path: '/auth/login' });
        } else {
            if (to.meta.adminAuth) {
                if (role === "Admin") {
                    return next();
                } else {
                    router.push({ path: '/' });
                }
            } else if (to.meta.OwnerAuth) {
                if (role === "Owner") {
                    return next();
                } else {
                    router.push({ path: '/' });
                }
            }
        }
    } else {
        return next();
    }

});

export default router;
